echo ">> Install vagrant insecure public key for ssh access"
install --directory --owner=vagrant --group=users --mode=0700 /home/vagrant/.ssh
echo 'ssh-rsa  vagrant insecure public key' > /home/vagrant/.ssh/authorized_keys
chown vagrant:users /home/vagrant/.ssh/authorized_keys
chmod 0600 /home/vagrant/.ssh/authorized_keys
